// Contract Registry - Manages provider and consumer registrations
import { Database } from "./kuzu_wrapper.ts";

export interface ProviderRegistration {
  uri: string;
  inputSchemaPath: string;
  outputSchemaPath: string;
  endpoint?: string;
  metadata?: any;
}

export interface ConsumerRegistration {
  uri: string;
  expectsInputSchemaPath: string;
  expectsOutputSchemaPath: string;
}

export class Registry {
  constructor(private db: Database) {}

  async registerProvider(data: ProviderRegistration) {
    // Read and validate schema files
    const schema = await this.loadSchemaFromPaths(
      data.inputSchemaPath,
      data.outputSchemaPath
    );

    // Store in graph DB
    await this.db.query(`
      MERGE (p:Provider {uri: $uri})
      SET p.endpoint = $endpoint,
          p.metadata = $metadata
      WITH p
      MERGE (s:Schema {
        input: $input,
        output: $output,
        hash: $hash
      })
      MERGE (p)-[:PROVIDES]->(s)
    `, {
      uri: data.uri,
      endpoint: data.endpoint || null,
      metadata: JSON.stringify(data.metadata || {}),
      input: JSON.stringify(schema.input),
      output: JSON.stringify(schema.output),
      hash: this.hashSchema(schema)
    });

    return {
      status: "registered",
      provider: data.uri,
      schema // Return loaded schema for confirmation
    };
  }

  async registerConsumer(data: ConsumerRegistration) {
    // Read and validate schema files
    const expects = await this.loadSchemaFromPaths(
      data.expectsInputSchemaPath,
      data.expectsOutputSchemaPath
    );

    // Store in graph DB
    await this.db.query(`
      MERGE (c:Consumer {uri: $uri})
      WITH c
      MERGE (s:Schema {
        input: $input,
        output: $output,
        hash: $hash
      })
      MERGE (c)-[:EXPECTS]->(s)
    `, {
      uri: data.uri,
      input: JSON.stringify(expects.input),
      output: JSON.stringify(expects.output),
      hash: this.hashSchema(expects)
    });

    // Trigger matching for new consumer
    await this.findAndCreateMatches(data.uri);

    return {
      status: "registered",
      consumer: data.uri,
      expects // Return loaded schema for confirmation
    };
  }

  private async findAndCreateMatches(consumerUri: string) {
    // Find compatible providers - simplified for MVP
    // In real implementation, match based on schema compatibility
    const result = await this.db.query(`
      MATCH (c:Consumer {uri: $uri})
      MATCH (p:Provider)
      MERGE (c)-[r:CAN_CALL]->(p)
      SET r.transform = $defaultTransform
      RETURN p.uri as provider
    `, {
      uri: consumerUri,
      defaultTransform: JSON.stringify(this.generateDefaultTransform())
    });

    return result;
  }

  private hashSchema(schema: any): string {
    return JSON.stringify(schema); // Simple hash for now
  }

  private generateDefaultTransform() {
    // Simple field mapping for now
    return {
      output: {
        "location": "city",
        "city": "location"
      },
      input: {
        "temperature": "temp",
        "humidity": "humid",
        "location": "city",
        "temp": "temperature",
        "humid": "humidity",
        "city": "location"
      }
    };
  }

  private async loadSchemaFromPaths(inputPath: string, outputPath: string) {
    try {
      // Convert relative paths to absolute
      const resolvedInputPath = this.resolvePath(inputPath);
      const resolvedOutputPath = this.resolvePath(outputPath);

      // Read files
      let inputContent, outputContent;
      try {
        inputContent = await Deno.readTextFile(resolvedInputPath);
      } catch (error) {
        if (error instanceof Deno.errors.NotFound) {
          throw new Error(`Schema file not found: ${resolvedInputPath}`);
        }
        throw error;
      }

      try {
        outputContent = await Deno.readTextFile(resolvedOutputPath);
      } catch (error) {
        if (error instanceof Deno.errors.NotFound) {
          throw new Error(`Schema file not found: ${resolvedOutputPath}`);
        }
        throw error;
      }

      // Parse JSON
      let inputSchema, outputSchema;
      try {
        inputSchema = JSON.parse(inputContent);
      } catch {
        throw new Error(`Invalid JSON in file: ${resolvedInputPath}`);
      }

      try {
        outputSchema = JSON.parse(outputContent);
      } catch {
        throw new Error(`Invalid JSON in file: ${resolvedOutputPath}`);
      }

      // Validate JSON Schema format
      this.validateJsonSchema(inputSchema, resolvedInputPath);
      this.validateJsonSchema(outputSchema, resolvedOutputPath);

      return {
        input: inputSchema,
        output: outputSchema
      };
    } catch (error) {
      // Re-throw with appropriate message
      throw error;
    }
  }

  private resolvePath(path: string): string {
    // Handle relative paths
    if (path.startsWith("./") || path.startsWith("../")) {
      return new URL(path, `file://${Deno.cwd()}/`).pathname;
    }
    return path;
  }

  private validateJsonSchema(schema: any, path: string): void {
    // Basic JSON Schema validation
    if (typeof schema !== "object" || schema === null) {
      throw new Error(`Invalid JSON Schema in ${path}: must be an object`);
    }

    // Check for type or properties (basic schema structure)
    if (!schema.type && !schema.properties && !schema.$ref && !schema.allOf && !schema.anyOf && !schema.oneOf) {
      throw new Error(`Invalid JSON Schema in ${path}: missing type or properties`);
    }
  }
}

