    14→import { serve } from "https://deno.land/std@0.208.0/http/server.ts";
    15→import { handler } from "../../src/main.ts";
    16→
    17→const CONTRACT_SERVICE_URL = "http://localhost:8000/rpc";
    18→
    19→// JSON-RPC2 helper
