    76→      },
    77→      required: ["temperature", "humidity", "location"]
    78→    })
    79→  );
    80→  
    81→  // Create Dashboard schema files
    82→  await Deno.writeTextFile(
    83→    `${testDir}/dashboard-expects-input.schema.json`,
    84→    JSON.stringify({
    85→      type: "object",
    86→      properties: {
    87→        temp: { type: "number" },
    88→        humid: { type: "number" },
    89→        city: { type: "string" }
    90→      },
    91→      required: ["temp", "humid", "city"]
    92→    })
    93→  );
    94→  await Deno.writeTextFile(
    95→    `${testDir}/dashboard-expects-output.schema.json`,
    96→    JSON.stringify({
    97→      type: "object",
    98→      properties: {
    99→        city: { type: "string" }
   100→      },
   101→      required: ["city"]
   102→    })
   103→  );
   104→
   105→  // Step 1: Weather Service（Provider）の契約登録
   106→  await t.step("Weather Serviceを登録", async () => {
   107→    const result = await rpcCall("contract.register", {
   108→      type: "provider",
   109→      uri: "services/weather/v1",
   110→      inputSchemaPath: `${testDir}/weather-input.schema.json`,
