   112→      endpoint: "http://localhost:9001/weather"
   113→    });
   114→
   115→    assertEquals(result.status, "registered");
   116→    assertEquals(result.provider, "services/weather/v1");
   117→    assert(Array.isArray(result.matches), "Should return matches array");
   118→  });
   119→
   120→  // Step 2: Dashboard（Consumer）の契約登録
   121→  await t.step("Dashboard Consumerを登録", async () => {
   122→    const result = await rpcCall("contract.register", {
   123→      type: "consumer",
   124→      uri: "ui/dashboard/v2",
   125→      expectsInputSchemaPath: `${testDir}/dashboard-expects-output.schema.json`,
   126→      expectsOutputSchemaPath: `${testDir}/dashboard-expects-input.schema.json`
   127→    }, 2);
   128→
   129→    assertEquals(result.status, "registered");
   130→    assertEquals(result.consumer, "ui/dashboard/v2");
   131→    assert(Array.isArray(result.providers), "Should return providers array");
   132→    assertEquals(result.providers.length, 1);
   133→    assertEquals(result.providers[0].uri, "services/weather/v1");
   134→  });
   135→
   136→
   137→  // Step 4: 変換テスト（ドライラン）
   138→  await t.step("変換のドライランテスト", async () => {
   139→    const result = await rpcCall("contract.test", {
   140→      from: "ui/dashboard/v2",
   141→      to: "services/weather/v1",
   142→      testData: { city: "Tokyo" },
   143→      dryRun: true
   144→    }, 4);
   145→
   146→    assert(Array.isArray(result.steps));
   147→    assertEquals(result.steps[0].step, "input");
   148→    assertEquals(result.steps[0].data, { city: "Tokyo" });
