   175→      const result = await rpcCall("contract.call", {
   176→        from: "ui/dashboard/v2",
   177→        data: { city: "Tokyo" }
   178→      }, 6);
   179→
   180→      // Check response structure
   181→      assert(result.data);
   182→      assert(result.meta);
   183→      
   184→      // Check transformed data
   185→      assertEquals(result.data.temp, 25.5);
   186→      assertEquals(result.data.humid, 60);
   187→      assertEquals(result.data.city, "Tokyo");
