packages.${system}.default = pkgs.stdenv.mkDerivation {
  pname = "contract-service";
  version = "0.1.0";

  src = ./.;

  buildInputs = with pkgs; [ deno kuzu ];

  buildPhase = ''
    export DENO_DIR=$PWD/.deno
    deno compile \
      --allow-net \
      --allow-read \
      --allow-write \
      --output=contract-service \
      src/main.ts
  '';

  installPhase = ''
    mkdir -p $out/bin
    cp contract-service $out/bin/
  '';
};

