async registerApp(appId: string, { inputSchemaPath, outputSchemaPath }: SchemaConfig): Promise<void> {
  if (!inputSchemaPath || !outputSchemaPath) {
    throw new Error('Both inputSchemaPath and outputSchemaPath are required');
  }

  const result = await this.loadSchemaFromPaths(inputSchemaPath, outputSchemaPath);
  if (!result.ok) {
    throw new Error(`Failed to load schemas: ${result.error.message}`);
  }

  this.apps.set(appId, {
    inputSchema: result.data.inputSchema,
    outputSchema: result.data.outputSchema
  });
}

edits.new_string:
async registerApp(appId: string, { inputSchemaPath, outputSchemaPath }: SchemaConfig): Promise<Result<void, RegistryError>> {
  if (!inputSchemaPath || !outputSchemaPath) {
    return {
      ok: false,
      error: {
        type: 'MISSING_PARAMETER',
        message: 'Both inputSchemaPath and outputSchemaPath are required'
      }
    };
  }

  const result = await this.loadSchemaFromPaths(inputSchemaPath, outputSchemaPath);
  if (!result.ok) {
    return { ok: false, error: result.error };
  }

  this.apps.set(appId, {
    inputSchema: result.data.inputSchema,
    outputSchema: result.data.outputSchema
  });

  return { ok: true, data: undefined };
}

