        providers: providers.map(p => ({
          uri: p.provider,
          endpoint: p.endpoint,
          transformPreview: {
            forward: { city: "location" }, // TODO: Get actual transform
            reverse: { temp: "temperature", humidity: "humid", location: "city" }
          }
        }))
