    flake-utils.lib.eachDefaultSystem (system:
      let
        pkgs = nixpkgs.legacyPackages.${system};
      in
      {
        # 共通のPython環境を提供
        packages.pythonEnv = pkgs.python312.withPackages (ps: with ps; [
          kuzu
          pytest
        ]);
      });
}

